        val sp = cookies.split("; ")
        val spl = sp.filter { it.startsWith("user.token") }
        if (spl.isNotEmpty()) {
            val userToken = spl[0].removePrefix("user.token=")
            viewModelScope.launch {
                if (convertUserId(userToken))
                    hopinNavigator.mainToStreamer()
                else {
