        "component_code": "CREATE TABLE oauth_refresh_tokens (\n  refresh_token       VARCHAR(40)     NOT NULL,\n  client_id           VARCHAR(80)     NOT NULL,\n  user_id             VARCHAR(80),\n  expires             TIMESTAMP       NOT NULL,\n  scope               VARCHAR(4000),\n  PRIMARY KEY (refresh_token)\n);",
        "component_description": "This table stores OAuth refresh tokens which allow clients to obtain new access tokens when the current ones expire. Each refresh token is linked to a client ID, user ID, expiration timestamp, and scope of permissions."
      },
      {
        "component_type": "TABLE_DEFINITION",
        "component_name": "oauth_users",
        "component_code": "CREATE TABLE oauth_users (\n  username            VARCHAR(80),\n  password            VARCHAR(80),\n  first_name          VARCHAR(80),\n  last_name           VARCHAR(80),\n  email               VARCHAR(80),\n  email_verified      BOOLEAN,\n  scope               VARCHAR(4000),\n  PRIMARY KEY (username)\n);",
        "component_description": "This table contains user information for the OAuth system, including usernames, passwords, names, emails, and verification status. It also stores a scope of permissions associated with each user."
      },
      {
        "component_type": "TABLE_DEFINITION",
        "component_name": "oauth_scopes",
        "component_code": "CREATE TABLE oauth_scopes (\n  scope               VARCHAR(80)     NOT NULL,\n  is_default          BOOLEAN,\n  PRIMARY KEY (scope)\n);",
        "component_description": "This table defines the different scopes of permissions available in the OAuth system. Each scope has a name and an indicator whether it is a default scope that can be automatically granted without user consent."
      },
      {
        "component_type": "TABLE_DEFINITION",
        "component_name": "oauth_jwt",
        "component_code": "CREATE TABLE oauth_jwt (\n  client_id           VARCHAR(80)     NOT NULL,\n  subject             VARCHAR(80),\n  public_key          VARCHAR(2000)   NOT NULL\n);",
        "component_description": "This table holds JSON Web Token (JWT) configuration details for clients, including the client ID, JWT subject, and the public key used to verify JWT signatures. It is used in systems that utilize JWTs for authentication and authorization."
      }
